import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import { useDispatch, useSelector } from 'react-redux'

import { fetchRegionStart } from '../../../../redux/config/config.actions'
import { delRegionStart } from '../../../../redux/config/config.actions'
import { selectRegionList } from '../../../../redux/config/config.selector'
import Modal from '@material-ui/core/Modal';


const useStyles = makeStyles({
    table: {
        minWidth: 400,
    },
    paper: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #fff',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    }
});


function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}


export default function BasicTable() {
    const dispatch = useDispatch()
    const regData = useSelector(state => selectRegionList(state))

    const classes = useStyles();


    useEffect(() => {
        dispatch(fetchRegionStart())
    }, [])

    console.log(regData)
    const noPointer = { cursor: 'default' };

    return (
        <TableContainer component={Paper} style={{ marginTop: "1.5em" }}>
            <Table className={classes.table} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>Available Regions</TableCell>
                        <TableCell></TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {regData.map((row) => (
                        <TableRow key={row.id}>
                            <TableCell component="th" scope="row">
                                {row.regname}
                            </TableCell>
                            <TableCell>
                                <DeleteForeverIcon style={noPointer} fontSize="medium" onClick={() => { dispatch(delRegionStart(row)); window.location.reload() }}></DeleteForeverIcon>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}
