import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import { useDispatch, useSelector } from 'react-redux'

import { fetchRegionStart } from '../../../../redux/config/config.actions'
import { delRegionStart } from '../../../../redux/config/config.actions'
import { selectRegionList } from '../../../../redux/config/config.selector'

const useStyles = makeStyles({
    table: {
        minWidth: 400,
    },
});


export default function BasicTable() {
    const dispatch = useDispatch()
    const regData = useSelector(state => selectRegionList(state))
    const [delregion, setDelRegion] = useState(true);
    const classes = useStyles();


    useEffect(() => {
        dispatch(fetchRegionStart())
    }, [delregion])

    console.log(regData)
    const noPointer = { cursor: 'default' };

    return (
        <TableContainer component={Paper} style={{ marginTop: "1.5em" }}>
            <Table className={classes.table} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>Available Regions</TableCell>
                        <TableCell></TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {regData.map((row) => (
                        <TableRow key={row.id}>
                            <TableCell component="th" scope="row">
                                {row.regname}
                            </TableCell>
                            <TableCell>
                                <DeleteForeverIcon style={noPointer} fontSize="medium" onClick={() => { dispatch(delRegionStart(row)); setDelRegion(!delregion) }}></DeleteForeverIcon>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
}
